console.log("Starting Enmap-JSON test...");
var e = require('./index.js');
var Enmap = require('enmap');
var db = new Enmap({
  provider: new e({
    path: "./test.json"
  })
});
console.log("Adding a key called foo to the test json file with the value bar.");
var x = parseInt(Date.now());
db.set("foo", "bar");
var z = parseInt(Date.now());
console.log("Task finished. "+ x - z +"ms.\nKey exists: "+ db.has("foo") +"\nValue: "+ db.get("foo") +"\nKey name: foo");
console.log("Now deleting foo key.");
var y = Date.now();
db.delete("foo");
var s = Date.now();
console.log("Task finished. "+ y - s +"ms. \nKey exists: "+ db.has("foo"));
console.log("Basic test finished with success.");
